// src/App.js
import React, { useState } from "react";
import Papa from "papaparse";
import Flashcard from "./components/Flashcard";

function App() {
  const [cards, setCards] = useState([]);
  const [finished, setFinished] = useState(false);
  const [stats, setStats] = useState({ total: 0, swipes: 0, knew: 0, didntKnow: 0 });
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        const parsedCards = results.data
          .filter((row) => row.question || row.Question)
          .map((row) => ({
            question: (row.question || row.Question || "").trim(),
            answer: (row.answer || row.Answer || "").trim(),
          }))
          .filter((card) => card.question && card.answer);

        setCards(parsedCards);
        setFinished(false);
        setStats({ total: parsedCards.length, swipes: 0, knew: 0, didntKnow: 0 });
        setCurrentIndex(0);
      },
    });
  };

  const handleSwipe = (direction) => {
    setStats((prev) => ({
      ...prev,
      swipes: prev.swipes + 1,
      knew: prev.knew + (direction === "right" ? 1 : 0),
      didntKnow: prev.didntKnow + (direction === "left" ? 1 : 0),
    }));

    if (currentIndex + 1 < cards.length) {
      setCurrentIndex((prev) => prev + 1);
    } else {
      setFinished(true);
    }
  };

  return (
    <div className="p-6 text-center">
      <h1 className="text-3xl font-bold mb-6">📚 Flashcard App</h1>

      {!cards.length && !finished && (
        <div className="mb-6">
          <input
            type="file"
            accept=".csv"
            onChange={handleFileUpload}
            className="mb-4"
          />
          <p className="text-gray-600">
            Upload a CSV file with <b>question</b> and <b>answer</b> columns
          </p>
        </div>
      )}

      {!finished && cards.length > 0 && (
        <Flashcard
          key={currentIndex}
          question={cards[currentIndex].question}
          answer={cards[currentIndex].answer}
          onSwipe={handleSwipe}
        />
      )}

      {finished && (
        <div className="bg-white p-8 rounded-2xl shadow-2xl w-96 mx-auto">
          <h2 className="text-2xl font-bold mb-4">🎉 Deck Completed!</h2>
          <p className="mb-2">Total Cards: {stats.total}</p>
          <p className="mb-2">Total Swipes: {stats.swipes}</p>
          <p className="mb-2">✅ Knew it: {stats.knew}</p>
          <p className="mb-2">❌ Didn’t know: {stats.didntKnow}</p>
          <p className="mb-6">
            Efficiency:{" "}
            {stats.total > 0
              ? Math.round((stats.knew / stats.total) * 100)
              : 0}
            %
          </p>
          <button
            onClick={() => setFinished(false)}
            className="px-6 py-2 bg-blue-500 text-white rounded-lg shadow-md hover:bg-blue-600 transition"
          >
            Restart Deck
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
