// src/components/Flashcard.js
import React, { useState } from "react";
import { useSpring, animated as a } from "@react-spring/web";
import TinderCard from "react-tinder-card";
import "./Flashcard.css";

const Flashcard = ({ question, answer, onSwipe }) => {
  const [flipped, setFlipped] = useState(false);
  const [canSwipe, setCanSwipe] = useState(false); // ✅ lock swipes

  const { transform, opacity } = useSpring({
    opacity: flipped ? 1 : 0,
    transform: `perspective(1000px) rotateY(${flipped ? 180 : 0}deg)`,
    config: { mass: 5, tension: 500, friction: 80 },
  });

  const handleSeeAnswer = () => {
    setFlipped(true);
    setCanSwipe(true); // ✅ unlock swiping only after answer shown
  };

  const handleSeeQuestion = () => {
    setFlipped(false);
    setCanSwipe(false); // ✅ lock swiping again
  };

  return (
    <div className="flashcard-container">
      <TinderCard
        className="swipe"
        onSwipe={(dir) => {
          if (canSwipe && (dir === "left" || dir === "right")) {
            onSwipe(dir);
            setFlipped(false);
            setCanSwipe(false);
          }
        }}
        preventSwipe={canSwipe ? ["up", "down"] : ["up", "down", "left", "right"]} // ✅ disable swipes when locked
      >
        <div className="flashcard-inner">
          {/* Front (Question) */}
          <a.div
            className="flashcard front"
            style={{ opacity: opacity.to((o) => 1 - o), transform }}
          >
            <h2>{question}</h2>
            <button onClick={handleSeeAnswer}>See Answer</button>
          </a.div>

          {/* Back (Answer) */}
          <a.div
            className="flashcard back"
            style={{ opacity, transform, rotateY: "180deg" }}
          >
            <h2>{answer}</h2>
            <div className="button-group">
              <button onClick={handleSeeQuestion}>See Question</button>
              <p className="text-gray-500 text-sm">Now swipe left/right</p>
            </div>
          </a.div>
        </div>
      </TinderCard>
    </div>
  );
};

export default Flashcard;
