// src/components/Flashcard.js
import React, { useState } from "react";
import { motion, useAnimation } from "framer-motion";
import "./Flashcard.css";

const Flashcard = ({ question, answer, onSwipe }) => {
  const [flipped, setFlipped] = useState(false);
  const [canSwipe, setCanSwipe] = useState(false);
  const controls = useAnimation();

  // Handle swipe end
  const handleDragEnd = (event, info) => {
    if (!canSwipe) return; // block swiping until answer is seen

    if (info.offset.x > 100) {
      onSwipe("right"); // Knew it
    } else if (info.offset.x < -100) {
      onSwipe("left"); // Didn’t know
    } else {
      // Snap back to center if swipe not strong
      controls.start({ x: 0, y: 0, rotate: 0 });
    }
  };

  return (
    <motion.div
      className="flashcard-wrapper"
      drag={canSwipe ? "x" : false}
      dragConstraints={{ left: 0, right: 0 }}
      onDragEnd={handleDragEnd}
      animate={controls}
      whileDrag={{ rotate: canSwipe ? (flipped ? 15 : 0) : 0 }}
    >
      <div className={`flashcard ${flipped ? "flipped" : ""}`}>
        {/* Front side (Question) */}
        <div className="front">
          <h2>{question}</h2>
          <button
            onClick={() => {
              setFlipped(true);
              setCanSwipe(true); // allow swipe after seeing answer
            }}
          >
            See Answer
          </button>
        </div>

        {/* Back side (Answer) */}
        <div className="back">
          <h2>{answer}</h2>
          <button
            onClick={() => {
              setFlipped(false);
              setCanSwipe(false); // reset swipe lock
              controls.start({ x: 0, y: 0, rotate: 0 }); // reset position
            }}
          >
            See Question
          </button>
        </div>
      </div>
    </motion.div>
  );
};

export default Flashcard;
