// src/App.js
import React, { useState } from "react";
import Papa from "papaparse";
import Flashcard from "./components/Flashcard";

function App() {
  const [cards, setCards] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [finished, setFinished] = useState(false);
  const [stats, setStats] = useState({
    total: 0,
    swipes: 0,
    knew: 0,
    didntKnow: 0,
  });

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        const parsedCards = results.data
          .filter((row) => row.question || row.Question)
          .map((row) => ({
            question: (row.question || row.Question || "").trim(),
            answer: (row.answer || row.Answer || "").trim(),
          }))
          .filter((card) => card.question && card.answer);

        setCards(parsedCards);
        setCurrentIndex(0);
        setFinished(false);
        setStats({
          total: parsedCards.length,
          swipes: 0,
          knew: 0,
          didntKnow: 0,
        });
      },
    });
  };

  const handleSwipe = (direction) => {
    setStats((prev) => ({
      ...prev,
      swipes: prev.swipes + 1,
      knew: direction === "right" ? prev.knew + 1 : prev.knew,
      didntKnow: direction === "left" ? prev.didntKnow + 1 : prev.didntKnow,
    }));

    const currentCard = cards[currentIndex];

    if (direction === "right") {
      // ‚úÖ Correct ‚Üí remove the card
      const newDeck = cards.filter((_, idx) => idx !== currentIndex);
      if (newDeck.length === 0) {
        setFinished(true);
      } else {
        setCards(newDeck);
        setCurrentIndex(currentIndex % newDeck.length);
      }
    } else if (direction === "left") {
      // ‚ùå Incorrect ‚Üí move card to end
      const newDeck = [
        ...cards.filter((_, idx) => idx !== currentIndex),
        currentCard,
      ];
      setCards(newDeck);
      setCurrentIndex(currentIndex % newDeck.length);
    }
  };

  return (
    <div className="p-6 text-center">
      <h1 className="text-3xl font-bold mb-6">üìö Flashcard App</h1>

      {/* Home screen (upload) */}
      {!cards.length && !finished && (
        <div className="mb-6">
          <input
            type="file"
            accept=".csv"
            onChange={handleFileUpload}
            className="mb-4"
          />
          <p className="text-gray-600">
            Upload a CSV file with <b>question</b> and <b>answer</b> columns
          </p>
        </div>
      )}

      {/* Deck in progress */}
      {!finished && cards.length > 0 && (
        <div>
          {/* Back button */}
          <button
            onClick={() => {
              setCards([]);
              setFinished(false);
              setCurrentIndex(0);
              setStats({
                total: 0,
                swipes: 0,
                knew: 0,
                didntKnow: 0,
              });
            }}
            className="px-4 py-2 mb-4 bg-gray-500 text-white rounded-lg shadow-md hover:bg-gray-600 transition"
          >
            ‚¨Ö Back to Home
          </button>

          <Flashcard
            key={currentIndex + "-" + cards.length}
            question={cards[currentIndex].question}
            answer={cards[currentIndex].answer}
            onSwipe={handleSwipe}
          />
        </div>
      )}

      {/* Deck completed */}
      {finished && (
        <div className="bg-white p-8 rounded-2xl shadow-2xl w-96 mx-auto">
          <h2 className="text-2xl font-bold mb-4">üéâ Deck Completed!</h2>
          <p className="mb-2">Total Cards: {stats.total}</p>
          <p className="mb-2">Total Swipes: {stats.swipes}</p>
          <p className="mb-2 text-green-600 font-semibold">
            ‚úÖ Knew it: {stats.knew}
          </p>
          <p className="mb-6 text-red-600 font-semibold">
            ‚ùå Didn‚Äôt Know (repeated until correct): {stats.didntKnow}
          </p>
          <button
            onClick={() => {
              setFinished(false);
              setCurrentIndex(0);
              setStats({
                total: cards.length,
                swipes: 0,
                knew: 0,
                didntKnow: 0,
              });
            }}
            className="px-6 py-2 bg-blue-500 text-white rounded-lg shadow-md hover:bg-blue-600 transition"
          >
            Restart Deck
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
