// src/components/Flashcard.js
import React, { useState } from "react";
import { motion } from "framer-motion";

const Flashcard = ({ question, answer, onSwipe }) => {
  const [flipped, setFlipped] = useState(false);

  return (
    <div className="flex justify-center items-center">
      <div className="relative w-80 h-96 perspective">
        <motion.div
          className="absolute w-full h-full rounded-xl shadow-2xl cursor-pointer"
          animate={{ rotateY: flipped ? 180 : 0 }}
          transition={{ duration: 0.6 }}
          style={{ transformStyle: "preserve-3d" }}
        >
          {/* Front (Question) */}
          <div className="absolute w-full h-full bg-white rounded-xl backface-hidden flex flex-col justify-center items-center p-6">
            <h2 className="text-xl font-semibold mb-4">{question}</h2>
            <button
              onClick={() => setFlipped(true)}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600"
            >
              See Answer
            </button>
          </div>

          {/* Back (Answer) */}
          <div className="absolute w-full h-full bg-gray-100 rounded-xl backface-hidden flex flex-col justify-center items-center p-6"
               style={{ transform: "rotateY(180deg)" }}>
            <h2 className="text-xl font-semibold mb-4">{answer}</h2>
            <div className="flex space-x-4">
              <button
                onClick={() => {
                  setFlipped(false);
                  onSwipe("right");
                }}
                className="px-4 py-2 bg-green-500 text-white rounded-lg shadow hover:bg-green-600"
              >
                ✅ Knew it
              </button>
              <button
                onClick={() => {
                  setFlipped(false);
                  onSwipe("left");
                }}
                className="px-4 py-2 bg-red-500 text-white rounded-lg shadow hover:bg-red-600"
              >
                ❌ Didn’t Know
              </button>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default Flashcard;
