// src/components/Flashcard.js
import React, { useState } from "react";
import { motion, useAnimation } from "framer-motion";
import { useSpring, animated as a } from "@react-spring/web";
import "./Flashcard.css";

const Flashcard = ({ question, answer, onSwipe }) => {
  const [flipped, setFlipped] = useState(false);
  const [canSwipe, setCanSwipe] = useState(false);
  const controls = useAnimation();

  const { transform, opacity } = useSpring({
    opacity: flipped ? 1 : 0,
    transform: `perspective(600px) rotateY(${flipped ? 180 : 0}deg)`,
    config: { mass: 5, tension: 500, friction: 80 },
  });

  const handleDragEnd = (_, info) => {
    if (!canSwipe) return; // prevent swipe before "See Answer"
    if (info.offset.x > 100) {
      onSwipe("right"); // knew it
    } else if (info.offset.x < -100) {
      onSwipe("left"); // didn't know
    } else {
      controls.start({ x: 0, y: 0, rotate: 0 }); // snap back
    }
  };

  return (
    <motion.div
      className="flashcard-container"
      drag={canSwipe ? "x" : false}
      onDragEnd={handleDragEnd}
      animate={controls}
      whileTap={{ scale: 1.05 }}
    >
      {/* Front side (Question) */}
      <a.div
        className="flashcard front"
        style={{ opacity: opacity.to((o) => 1 - o), transform }}
      >
        <h2>{question}</h2>
        <button onClick={() => { setFlipped(true); setCanSwipe(true); }}>
          See Answer
        </button>
      </a.div>

      {/* Back side (Answer) */}
      <a.div
        className="flashcard back"
        style={{ opacity, transform, rotateY: "180deg" }}
      >
        <h2>{answer}</h2>
        <p className="swipe-hint">⬅️ Swipe left = Didn’t Know | Swipe right = Knew it ➡️</p>
      </a.div>
    </motion.div>
  );
};

export default Flashcard;
